<script lang="ts" setup>
  const banner = ref<HTMLDivElement>()

  const _props = withDefaults(defineProps<{ active: boolean }>(), { active: false }); 

  const render = () => {
    banner.value?.classList.remove('invisible')
    banner.value?.classList.add('active')
  }

  onMounted(() => {
    const handleScroll = () => {
      if(_props.active && isInViewport(banner.value)) {
        
        render()

        window.removeEventListener('scroll', handleScroll);
      }
    }

    if(_props.active && banner && isInViewport(banner.value)) {
      render()
    }

    window.addEventListener('scroll', handleScroll)
  })
</script>

